import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import Axios from "axios";

const Task = () => {
    const {id, taskId} = useParams();
    const [task, setTask] = useState({});

    useEffect(() => {
        Axios.get(`http://localhost:3001/api/tasks/${taskId}`).then(response => {
            setTask(response.data);
        })
    }, [])

    return (
        <div>
            <div className="container py-4">
                <header className="pb-3 mb-4 border-bottom">
                    <a href="#" className="d-flex align-items-center text-dark text-decoration-none">
                        <span className="fs-4">{task.name}</span>
                    </a>
                </header>

                <div className="p-5 mb-4 bg-light rounded-3">
                    <div className="container-fluid py-5">
                        <h1 className="display-5 fw-bold">Custom jumbotron</h1>
                        <p className="col-md-8 fs-4">Using a series of utilities, you can create this jumbotron,
                            just like the one in previous versions of Bootstrap. Check out the examples below for
                            how you can remix and restyle it to your liking.</p>
                        <button className="btn btn-primary btn-lg" type="button">Example button</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Task;
