import React, {useState} from 'react';
import {Form, Button} from "react-bootstrap";
import Axios from "axios";
import {useHistory, useParams} from "react-router-dom";

const CreateUpdateTask = () => {
    const {id, taskId} = useParams();
    let history = useHistory();
    const [taskName, setTaskName] = useState("");
    const [taskTopic, setTaskTopic] = useState("");

    async function createTask() {
        const newTask = {name: taskName, topic: taskTopic, userId: id}
        await Axios.post("http://localhost:3001/api/tasks", newTask).then(
            history.push(`/user/${id}`)
        );
    }

    async function updateTask() {
        await Axios.get(`http://localhost:3001/api/tasks/${id}`).then(response => {
                setTaskName(response.data.name);
                setTaskTopic(response.data.topic);
            }
        );
    }

    return (
        <Form>
            <Form.Group className="mb-3">
                <Form.Label>Task name</Form.Label>
                <Form.Control onChange={(e) => {
                    setTaskName(e.target.value)
                }} type="text" placeholder="Enter task name"/>
            </Form.Group>

            <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <Form.Control onChange={(e) => {
                    setTaskTopic(e.target.value)
                }} type="text" placeholder="Enter task topic"/>
            </Form.Group>
            <Button onClick={createTask} variant="primary" type="submit">
                Submit
            </Button>
            <Button style={{margin: "30px"}} onClick={() => {
                history.push(`/user/${id}`);
            }} variant="secondary" type="submit">
                Cancel
            </Button>
        </Form>
    );
};

export default CreateUpdateTask;
