import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import Axios from 'axios';
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, {textFilter} from 'react-bootstrap-table2-filter';
import {Card} from "react-bootstrap";

const User = () => {
    let {id} = useParams();
    const [user, setUser] = useState({});
    const [userTasks, setUserTasks] = useState([]);
    const columns = [{
        dataField: 'id',
        text: 'Task id',
        sort: true
    }, {
        dataField: 'name',
        text: 'Task name',
        sort: true,
        filter: textFilter()
    }, {
        dataField: 'topic',
        text: 'Task topic',
        sort: true,
        filter: textFilter()
    }];

    useEffect(() => {
        Axios.get(`http://localhost:3001/api/users/${id}`).then(response => {
            setUser(response.data);
            setUserTasks(response.data.tasks)
        })
    }, [])

    return (
        <div>
            <BootstrapTable keyField='id' data={userTasks} columns={columns}
                            filter={filterFactory()}/>
            <Card body>Solved tasks: {user.solvedTasks}</Card>
            <Card body>Created tasks: {user.createdTasks}</Card>
        </div>
    );
};

export default User;
