import React, {useEffect, useState} from 'react';
import {useParams} from "react-router-dom";
import Axios from 'axios';
import BootstrapTable from 'react-bootstrap-table-next';
import {Card, Col, ListGroup, Row} from "react-bootstrap";

const User = () => {
    let {id} = useParams();
    const [user, setUser] = useState({});
    const [userTasks, setUserTasks] = useState([]);
    const columns = [{
        dataField: 'id',
        text: 'Task id'
    }, {
        dataField: 'name',
        text: 'Task name'
    }, {
        dataField: 'topic',
        text: 'Task topic'
    }];

    useEffect(() => {
        Axios.get(`http://localhost:3001/api/users/${id}`).then(response => {
            setUser(response.data);
            {response.data.tasks.map((value, index) => {
                return <ListGroup.Item action onClick={() => {
                    history.push(`/user/${value.id}`)
                }}>
                    <Row><Col>{value.name}</Col></Row>
                </ListGroup.Item>
            })}
            console.log(response.data);
           // setUserTasks(response.data.tasks)
        })
    }, [])

    return (
        <div>
            {/*<BootstrapTable keyField='id' data={tasks} columns={columns}/>*/}
            <Card body>Solved tasks: {user.solvedTasks}</Card>
            <Card body>Created tasks: {user.createdTasks}</Card>
        </div>
    );
};

export default User;
