import React, {useEffect, useState} from 'react';
import {useHistory, useParams} from "react-router-dom";
import Axios from 'axios';
import BootstrapTable from 'react-bootstrap-table-next';
import filterFactory, {textFilter} from 'react-bootstrap-table2-filter';
import {Card, Button, Row} from "react-bootstrap";

const User = () => {
    let {id} = useParams();
    let history = useHistory();
    const [user, setUser] = useState({});
    const [userTasks, setUserTasks] = useState([]);
    const columns = [{
        dataField: 'id',
        text: 'Task id',
        sort: true
    }, {
        dataField: 'name',
        text: 'Task name',
        sort: true,
        filter: textFilter()
    }, {
        dataField: 'topic',
        text: 'Task topic',
        sort: true,
        filter: textFilter()
    }];

    const selectRow = {
        mode: 'checkbox',
        clickToSelect: true
    };

    useEffect(() => {
        Axios.get(`http://localhost:3001/api/users/${id}`).then(response => {
            setUser(response.data);
            setUserTasks(response.data.tasks)
        })
    }, [])

    async function addTask() {
        /*  await Axios.post("http://localhost:3001/api/tasks", {
              "name": "First task",
              "topic": "My Topic"
          })*/
        history.push(`/user/${id}/createUpdate`)
    }

    return (
        <div className="userInfo">
            <BootstrapTable bootstrap4={true} keyField='id' data={userTasks} columns={columns}
                            filter={filterFactory()} selectRow={selectRow}/>
            <Button style={{margin: "15px"}} variant="primary" onClick={addTask}>Add task</Button>
            <Button style={{margin: "15px"}} variant="danger">Delete task</Button>
            <Button style={{margin: "15px"}} variant="warning">Update task</Button>
            <Card body>Solved tasks: {user.solvedTasks}</Card>
            <Card body>Created tasks: {user.createdTasks}</Card>
        </div>
    );
};

export default User;
